import{_ as s,c as i,o as a,U as t}from"./chunks/framework.I0qYP7JU.js";const g=JSON.parse('{"title":"wordpress head中多余信息的去除","description":"","frontmatter":{"title":"wordpress head中多余信息的去除","tags":["wordpress"],"id":35,"author":"welpher.yu","categories":["blog"],"date":"2011-08-08T21:49:54.000Z"},"headers":[],"relativePath":"posts/2011/35.md","filePath":"posts/2011/35.md"}'),e={name:"posts/2011/35.md"},p=t('<p>wordpress中调用 wp_head()会生成很多信息，这些信息不是所有都有用，在网上搜来一些方法可以将这些信息去除。</p><p>很个主题一般来说都会有functions.php这个文件，打开这个文件，往里面添加些代码。</p><p>1、generator这个meta的去除方法（WordPress版本号的链接）：</p><div class="language-php vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">php</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">emove_action</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;wp_head&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">,</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;wp_generator&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">);</span></span></code></pre></div><p>2、去除 &lt;link rel=”EditURI”  标签（博客离线编辑器开放接口链接）：</p><div class="language-php vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">php</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">remove_action</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;wp_head&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">,</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;rsd_link&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">);</span></span></code></pre></div><p>3、去除 &lt;link rel=”wlwmanifest”…… 标签（离线编辑器Windows Live Writer链接）：</p><div class="language-php vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">php</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">remove_action</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;wp_head&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">,</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;wlwmanifest_link&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">);</span></span></code></pre></div>',8),h=[p];function n(l,d,r,k,o,c){return a(),i("div",null,h)}const _=s(e,[["render",n]]);export{g as __pageData,_ as default};
